&lt;h3 id=&#34;7-v-model&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#7-v-model&#34;&gt;#&lt;/a&gt; 7. v-model&lt;/h3&gt;
&lt;blockquote&gt;
&lt;p&gt;子组件&lt;/p&gt;
&lt;/blockquote&gt;
&lt;pre&gt;&lt;code class=&#34;language-vue&#34;&gt;&amp;lt;!--  --&amp;gt;
&amp;lt;template&amp;gt;
  &amp;lt;div&amp;gt;&amp;#123;&amp;#123; name &amp;#125;&amp;#125;&amp;#123;&amp;#123; age &amp;#125;&amp;#125;&amp;lt;/div&amp;gt;
  &amp;lt;button @click=&amp;quot;updateName&amp;quot;&amp;gt;修改姓名&amp;lt;/button&amp;gt;
&amp;lt;/template&amp;gt;

&amp;lt;script lang=&amp;quot;ts&amp;quot; setup&amp;gt;
let props = defineProps(&amp;#123;
  age: Number,
  name: String,
&amp;#125;);
let emits = defineEmits([&amp;quot;update:name&amp;quot;, &amp;quot;update:age&amp;quot;]);
let updateName = () =&amp;gt; &amp;#123;
  emits(&amp;quot;update:age&amp;quot;, 30);
  emits(&amp;quot;update:name&amp;quot;, &amp;quot;李四&amp;quot;);
&amp;#125;;
&amp;lt;/script&amp;gt;

&amp;lt;style lang=&amp;quot;less&amp;quot; scoped&amp;gt;&amp;lt;/style&amp;gt;

