  &amp;lt;div&amp;gt;&amp;lt;/div&amp;gt;
  &amp;lt;mychild v-model:name=&amp;quot;state.name&amp;quot; v-model:age=&amp;quot;state.age&amp;quot;&amp;gt;&amp;lt;/mychild&amp;gt;
&amp;lt;/template&amp;gt;

&amp;lt;script lang=&amp;quot;ts&amp;quot; setup&amp;gt;
import &amp;#123; reactive &amp;#125; from &amp;quot;vue&amp;quot;;
import mychild from &amp;quot;./b.vue&amp;quot;;
let info = &amp;#123;
  name: &#39;张三&#39;,
  age: 20
&amp;#125;
let state = reactive(info);

&amp;lt;/script&amp;gt;

