&lt;pre&gt;&lt;code class=&#34;language-vue&#34;&gt;&amp;lt;!--  --&amp;gt;
&amp;lt;template&amp;gt;
  &amp;lt;slot&amp;gt;&amp;lt;/slot&amp;gt;
  &amp;lt;slot name=&amp;quot;title&amp;quot;&amp;gt;&amp;lt;/slot&amp;gt;
  &amp;lt;slot name=&amp;quot;footer&amp;quot; :user=&amp;quot;state.user&amp;quot; :d=&amp;quot;state.d&amp;quot;&amp;gt;&amp;lt;/slot&amp;gt;
&amp;lt;/template&amp;gt;

&amp;lt;script lang=&amp;quot;ts&amp;quot; setup&amp;gt;
import &amp;#123; reactive &amp;#125; from &amp;quot;vue&amp;quot;;
let state = reactive(&amp;#123;
  user: &amp;#123; a: 1, b: 2 &amp;#125;,
  d: 1,
&amp;#125;);
&amp;lt;/script&amp;gt;

