  &amp;lt;mychild&amp;gt;
    &amp;lt;div&amp;gt;匿名插槽&amp;lt;/div&amp;gt;
    &amp;lt;template #title&amp;gt;
      &amp;lt;div&amp;gt;具名插槽&amp;lt;/div&amp;gt;
    &amp;lt;/template&amp;gt;
    &amp;lt;template #footer=&amp;quot;scope&amp;quot;&amp;gt;
      &amp;lt;div&amp;gt;作用域插槽&amp;#123;&amp;#123; scope.user &amp;#125;&amp;#125; &amp;#123;&amp;#123; scope.d &amp;#125;&amp;#125;&amp;lt;/div&amp;gt;
    &amp;lt;/template&amp;gt;
  &amp;lt;/mychild&amp;gt;
